 ShowDeviceInfo();
function ShowDeviceInfo() {
    var currDevice = datasources.selectedDevice.latestData();
    var promise = datasources.GetTypeSchema.sendData({
        "async": false,
        "deviceType": currDevice.type
    });

    var inputFieldType = {
        "boolean": "checkbox",
        "string": "text",
        "largestring": "textarea"

    }

    promise.then(function (data) {
        if (data.success) {
            var schema = JSON.parse(data.results.DATA[0]["schema"]);
            var formElements = [];
            for (var key in schema) {
                if (currDevice.hasOwnProperty(key)) {
                    var label = key;
                    formElements.push(createCustomFormElement(label, inputFieldType[schema[key]], currDevice[key]));
                }
            }
            var buttonForUpdate = `<div class="form-group">
        <button id="" type="submit" onclick="updateDevice()" class="btn btn-success">Update</button></div>`
            $("#myForm").empty().append(formElements.join('')).append(buttonForUpdate);
        }
        else{
            //$("#myForm").empty().append(`<p>`+data.results+` for `+datasources.selectedDevice.latestData().name +`</p>`);
        }
    });
}

function createCustomFormElement(label, inputType, initialValue) {
    var checked = "";
    var formLabel = toTitleCase(label);
    var inputTag = `<input id="` + label + `" class="form-control device-info" ` + checked + ` type = "` + inputType + `" name = "` + label + `" value = "` + initialValue + `" />`;
    if (typeof (initialValue) === "boolean") {
        checked = toString(initialValue);
    }
    else if (inputType === "largestring") {
        inputTag = `<textarea class="form-control" type="textarea" id="` + label + `" name="` + formLabel + `"
							 " rows="5">`+ initialValue + `</textarea>`
    }
    return `<div class="form-group div-` + label + `">
						<label class="col-sm-3 row">`+ formLabel + ` </label>
						<div class="col-sm-9 row">
							`+ inputTag + `
						</div>
                    </div>`;

}



function isFormValid() {
    return true;
}


window.updateDevice = function () {
    debugger;
    if (isFormValid()) {
        $('.device-info').each(function (i, obj) {
            currDevice[obj.name] = obj.value;
        });
        $(".div-UpdateResponse").remove();
        datasources.UpdateDevice.sendData({
            "deviceInfo": currDevice
        }).then(function (data) {
            var value = "";
            if (data.success) {
                value = "Success";
            }
            else {
                var err = JSON.parse(data.results).error;
                delete err.line;
                delete err.lowLevelError;
                value = JSON.stringify(err, undefined, 4);
            }
            var element = createCustomFormElement("UpdateResponse", "largestring", value);

            $("#myForm").append(element);

        });
    }
    else {
        alert('Invalid Device Data');
    }

}

function toTitleCase(str) {
    return str.replace(/[-_]/g, ' ').replace(/\w\S*/g, function (txt) { return txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase(); });
}
